library(tidytuesdayR)
library(dplyr)
library(ggplot2)
Sys.setenv(GITHUB_PAT ="")
tuesdata &lt;- tidytuesdayR::tt_load('2025-01-28')
water_insecurity_2022 &lt;- tuesdata$water_insecurity_2022
water_insecurity_2023 &lt;- tuesdata$water_insecurity_2023
water_insecurity_2022 &lt;- water_insecurity_2022 |&gt;  
 dplyr::mutate(
    geometry = purrr::map(geometry, \(geo) {
      eval(parse(text = geo))
    } )
  )
water_insecurity_2023 &lt;- water_insecurity_2023 |&gt; 
  dplyr::mutate(
    geometry = purrr::map(geometry, \(geo) {
      eval(parse(text = geo))
    } )
  )

top10_lacking &lt;- water_insecurity_2022 |&gt;
  mutate(greatest_lacking=percent_lacking_plumbing) |&gt;
  arrange(desc(greatest_lacking)) |&gt;
  slice_head(n=10)

ggplot(top10_lacking, aes(x=reorder(name, greatest_lacking), y=greatest_lacking))+
  geom_col(fill="pink")+
  coord_flip()+
  labs(
    title = "Top 10 areas lacking plumbing in the U.S. in 2022",
    x="County Name", 
    y="Percent Lacking Plumbing"
  ) +
